/*
 * @lc app=leetcode id=507 lang=javascript
 *
 * [507] Perfect Number
 */

// @lc code=start
/**
 * @param {number} num
 * @return {boolean}
 */
var checkPerfectNumber = function (num) {
  //  the first four perfect numbers are generated by the formula 2p−1(2p − 1), with p a prime number
  let sum = 0;
  //完全数除以自己一半，求余肯定会大于0，所以用num/2
  for (let j = 1; j <= num / 2; j++) {
    if (num % j === 0) {
      sum += j;
    }
  }
  return sum === num;
};
// @lc code=end
let num = 28;
console.log(checkPerfectNumber(num));
